vBar.effects.striped={init:function(){var a=this.getBar(),b=a.querySelectorAll("span");if(b.length>0)for(var c=0;c<b.length;++c)b.item(c).style.backgroundImage="repeating-linear-gradient(45deg, rgba(0, 0, 0, 0.20), rgba(0, 0, 0, 0.30) 10px, transparent 10px, transparent 20px, rgba(0, 0, 0, 0.20) 20px)";else a.style.backgroundImage="repeating-linear-gradient(45deg, rgba(0, 0, 0, 0.20), rgba(0, 0, 0, 0.30) 10px, transparent 10px, transparent 20px, rgba(0, 0, 0, 0.20) 20px)"}};